import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import DashboardUser from './pages/DashboardUser';
import DashboardDoctor from './pages/DashboardDoctor';
import Login from './pages/Login';
import Register from './pages/Register';
import Chat from './pages/Chat';
import ProtectedRoute from './components/ProtectedRoute';
import BatchPredictDoctor from './pages/BatchPredictDoctor';

function App() {
  const [user, setUser] = useState(null);

  // Side effect: check localStorage for token/user on mount
  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (stored) setUser(JSON.parse(stored));
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register />} />

        <Route path="/user/dashboard" element={
          <ProtectedRoute user={user} allowedRoles={["user"]}>
            <DashboardUser user={user} />
          </ProtectedRoute>
        } />

        <Route path="/doctor/dashboard" element={
          <ProtectedRoute user={user} allowedRoles={["doctor"]}>
            <DashboardDoctor user={user} />
          </ProtectedRoute>
        } />

        <Route path="/doctor/batch" element={
          <ProtectedRoute user={user} allowedRoles={["doctor"]}>
            <BatchPredictDoctor user={user} />
          </ProtectedRoute>
        } />

        <Route path="/chat" element={
          <ProtectedRoute user={user} allowedRoles={["user", "doctor"]}>
            <Chat user={user} />
          </ProtectedRoute>
        } />

        <Route path="/" element={
          user ? (
           user.role === "doctor" ? <Navigate to="/doctor/dashboard" /> : <Navigate to="/user/dashboard" />
          ) : <Navigate to="/login" />
        } />
      </Routes>
    </Router>
  );
}

export default App;
