// src/pages/Predict.jsx
import React, { useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import RiskForm from '../components/predict/RiskForm';
import PredictionResult from '../components/predict/PredictionResult';
import { savePredictionResult, downloadPredictionReport } from '../services/predict';
import LoadingSpinner from '../components/common/LoadingSpinner';

const Predict = () => {
  const [prediction, setPrediction] = useState(null);
  const [isSaving, setIsSaving] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);

  const handleSaveResult = async () => {
    if (!prediction) return;
    setIsSaving(true);
    try {
      await savePredictionResult(prediction.id);
      console.log('Result saved successfully');
    } catch (error) {
      console.error('Failed to save result:', error);
    } finally {
      setIsSaving(false);
    }
  };

  const handleDownloadReport = async () => {
    if (!prediction) return;
    setIsDownloading(true);
    try {
      const reportData = await downloadPredictionReport(prediction.id);
      const blob = new Blob([reportData], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `CardioGuard-Report-${prediction.id}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Failed to download report:', error);
    } finally {
      setIsDownloading(false);
    }
  };

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Cardiovascular Risk Assessment</h1>
          <p className="mt-2 text-lg text-gray-600">
            Enter your health information to calculate your risk of cardiovascular disease
          </p>
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div>
            <RiskForm onSubmit={setPrediction} />
          </div>
          <div>
            {prediction ? (
              <PredictionResult
                prediction={prediction}
                onSave={handleSaveResult}
                onDownload={handleDownloadReport}
              />
            ) : (
              <div className="bg-white rounded-xl shadow-lg p-8 text-center">
                <div className="bg-gray-200 border-2 border-dashed rounded-xl w-16 h-16 mx-auto" />
                <h3 className="mt-4 text-xl font-medium text-gray-900">No prediction yet</h3>
                <p className="mt-2 text-gray-500">
                  Fill out the form to see your cardiovascular risk assessment
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Predict;
