import api from './api';

export const getDashboardData = async (userRole) => {
  const endpoint = userRole === 'doctor' ? '/dashboard/doctor' : '/dashboard/patient';
  const response = await api.get(endpoint);
  return response.data;
};

export const getUserStatistics = async () => {
  const response = await api.get('/dashboard/patient');
  return response.data;
};

export const getSystemStatistics = async () => {
  const response = await api.get('/dashboard/doctor');
  return response.data;
};
