import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    fullName: '', // Changed from full_name
    password: '',
    confirmPassword: '',
    role: 'patient'
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { register } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate passwords match
    if (formData.password !== formData.confirmPassword) {
      return setError('Passwords do not match');
    }
    
    // Validate password length
    if (formData.password.length < 8) {
      return setError('Password must be at least 8 characters');
    }

    setError('');
    setLoading(true);
    
    try {
      await register({
        email: formData.email,
        fullName: formData.fullName, // Changed from full_name
        password: formData.password,
        role: formData.role
      });
    } catch (err) {
      setError('Failed to create an account: ' + (err.response?.data?.detail || 'Please try again'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md w-full space-y-8">
      {/* ... existing JSX ... */}
      <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
        {/* ... error display ... */}
        <div className="rounded-md shadow-sm -space-y-px">
          <div>
            <label htmlFor="full-name" className="sr-only">
              Full Name
            </label>
            <input
              id="full-name"
              name="fullName"  // Changed from full_name
              type="text"
              required
              className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm"
              placeholder="Full Name"
              value={formData.fullName}
              onChange={handleChange}
            />
          </div>
          {/* ... other fields ... */}
          <div>
            <label htmlFor="confirm-password" className="sr-only">
              Confirm Password
            </label>
            <input
              id="confirm-password"
              name="confirmPassword"  // Ensure correct name
              type="password"
              required
              className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
          </div>
        </div>
        {/* ... role selection ... */}
      </form>
      {/* ... existing JSX ... */}
    </div>
  );
};

export default RegisterForm;
