import React, { useState, useEffect, useRef } from 'react';
import { useChat } from '../../context/ChatContext';
import MessageBubble from './MessageBubble';
import { PaperAirplaneIcon, TrashIcon } from '@heroicons/react/24/outline';

const ChatInterface = ({ sessionId }) => {
  const {
    currentConversation,
    sendMessage,
    clearCurrentSession,
    switchSession,
    currentPrediction
  } = useChat();
  
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Switch to the provided session
  useEffect(() => {
    if (sessionId) {
      switchSession(sessionId);
    }
  }, [sessionId, switchSession]);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [currentConversation]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim() || isLoading || !sessionId) return;

    const messageText = message.trim();
    setMessage('');
    setIsLoading(true);

    try {
      await sendMessage(messageText, sessionId);
    } catch (error) {
      console.error('Failed to send message:', error);
      // Optionally show error toast
    } finally {
      setIsLoading(false);
    }
  };

  const handleClearConversation = () => {
    if (window.confirm('Are you sure you want to clear this conversation? This action cannot be undone.')) {
      clearCurrentSession();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-sm">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">
            CVD Risk Consultation
          </h3>
          {currentPrediction && (
            <p className="text-sm text-gray-600">
              Patient: {currentPrediction.patientId} | Risk: {currentPrediction.riskCategory}
            </p>
          )}
        </div>
        <div className="flex space-x-2">
          <button
            onClick={handleClearConversation}
            className="p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
            title="Clear conversation"
          >
            <TrashIcon className="h-5 w-5" />
          </button>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {currentConversation.length === 0 ? (
          <div className="text-center py-8">
            <div className="text-gray-400 mb-4">
              <svg className="mx-auto h-12 w-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">Start a conversation</h3>
            <p className="text-gray-500">
              Ask questions about cardiovascular risk assessment, treatment recommendations, or lifestyle advice.
            </p>
            {currentPrediction && (
              <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-800">
                  ðŸ’¡ I have context about the patient's risk assessment. Feel free to ask specific questions!
                </p>
              </div>
            )}
          </div>
        ) : (
          <>
            {currentConversation.map((msg) => (
              <MessageBubble 
                key={msg.id} 
                message={msg} 
                isUser={msg.role === 'user'}
              />
            ))}
            {isLoading && (
              <div className="flex justify-start">
                <div className="bg-gray-100 rounded-lg p-3 max-w-xs">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Persistence Status */}
      {currentConversation.length > 0 && (
        <div className="px-4 py-2 bg-gray-50 border-t border-gray-200">
          <p className="text-xs text-gray-500 text-center">
            ðŸ’¾ Conversation automatically saved - will persist across page navigation
          </p>
        </div>
      )}

      {/* Input */}
      <form onSubmit={handleSendMessage} className="p-4 border-t border-gray-200">
        <div className="flex space-x-3">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Ask about cardiovascular risk, treatment options, or lifestyle advice..."
            className="flex-1 border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            disabled={isLoading || !sessionId}
          />
          <button
            type="submit"
            disabled={!message.trim() || isLoading || !sessionId}
            className="bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
          >
            <PaperAirplaneIcon className="h-5 w-5" />
          </button>
        </div>
        {!sessionId && (
          <p className="text-xs text-red-500 mt-2">
            Please select or create a session to start chatting
          </p>
        )}
      </form>
    </div>
  );
};

export default ChatInterface;
