import React, { useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import ChatInterface from '../components/chat/ChatInterface';
import { MessageSquare } from 'lucide-react';

const Chat = () => {
  const [messages, setMessages] = useState([]);

  const sendMessage = async (message) => {
    const newMessage = {
      id: Date.now(),
      content: message,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    // Add user message immediately
    setMessages(prev => [...prev, newMessage]);

    // Simulate AI response
    setTimeout(() => {
      const aiResponse = {
        id: Date.now() + 1,
        content: generateHealthResponse(message),
        sender: 'ai',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, aiResponse]);
    }, 1000);
  };

  const generateHealthResponse = (userMessage) => {
    const responses = [
      "Based on your cardiovascular risk factors, I recommend maintaining a balanced diet rich in fruits, vegetables, and whole grains.",
      "Regular physical activity is crucial for heart health. Aim for at least 150 minutes of moderate-intensity exercise per week.",
      "Managing stress through relaxation techniques, meditation, or yoga can significantly benefit your cardiovascular health.",
      "It's important to monitor your blood pressure regularly and maintain it within healthy ranges.",
      "Avoiding smoking and limiting alcohol consumption are key factors in reducing cardiovascular disease risk."
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleNewChat = () => {
    setMessages([]);
  };

  return (
    <DashboardLayout>
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8 flex justify-between items-center">
          <div className="flex items-center">
            <MessageSquare className="w-8 h-8 text-blue-500 mr-3" />
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Health Assistant</h1>
              <p className="mt-2 text-lg text-gray-600">
                Get personalized advice about your cardiovascular health
              </p>
            </div>
          </div>
          <button
            onClick={handleNewChat}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
          >
            New Chat
          </button>
        </div>
        
        <div className="bg-white rounded-xl shadow-lg">
          <ChatInterface
            messages={messages}
            onSend={sendMessage}
          />
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Chat;
