import React from "react";
import { Link, useNavigate } from "react-router-dom";
import useAuth from "../../hooks/useAuth";

export default function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-white shadow px-6 py-3 flex items-center justify-between mb-6">
      <Link to="/" className="font-bold text-xl text-blue-600">CVD Risk Predictor</Link>
      <div>
        {user ? (
          <>
            <Link to="/dashboard" className="mr-4 hover:underline">Dashboard</Link>
            <Link to="/chat" className="mr-4 hover:underline">Chat</Link>
            <Link to="/profile" className="mr-4 hover:underline">Profile</Link>
            {user.role === "doctor" && (
              <Link to="/batch-predict" className="mr-4 hover:underline">Batch Predict</Link>
            )}
            <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-1 rounded">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="mr-4 hover:underline">Sign In</Link>
            <Link to="/register" className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
}
