import React, { useState } from "react";
import { createPrediction } from "../../services/predict";
import PredictionResult from "./PredictionResult";

export default function RiskForm({ onResult }) {
  const [values, setValues] = useState({
    age: "",
    gender: "",
    cholesterol: "",
    sys_bp: "",
    smoking: "",
    diabetes: ""
  });
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const handleChange = (e) => setValues(v => ({
    ...v, [e.target.name]: e.target.value
  }));
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const res = await createPrediction(values);
      setResult(res);
      if (onResult) onResult(res);
    } catch (err) {
      setError(err.response?.data?.detail || "Failed to compute prediction");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow">
      <h2 className="text-lg font-semibold mb-2">Check Your CVD Risk</h2>
      <form className="space-y-3" onSubmit={handleSubmit}>
        <input type="number" name="age" value={values.age} onChange={handleChange} placeholder="Age" required className="border px-2 py-1 w-full rounded" min={0} />
        <select name="gender" value={values.gender} onChange={handleChange} required className="border px-2 py-1 w-full rounded">
          <option value="">Select Gender</option>
          <option value="M">Male</option>
          <option value="F">Female</option>
        </select>
        <input name="cholesterol" value={values.cholesterol} onChange={handleChange} placeholder="Cholesterol" required className="border px-2 py-1 w-full rounded" />
        <input name="sys_bp" value={values.sys_bp} onChange={handleChange} placeholder="Systolic BP" required className="border px-2 py-1 w-full rounded" />
        <select name="smoking" value={values.smoking} onChange={handleChange} required className="border px-2 py-1 w-full rounded">
          <option value="">Smoking status</option>
          <option value="1">Smoker</option>
          <option value="0">Non-Smoker</option>
        </select>
        <select name="diabetes" value={values.diabetes} onChange={handleChange} required className="border px-2 py-1 w-full rounded">
          <option value="">Diabetes status</option>
          <option value="1">Yes</option>
          <option value="0">No</option>
        </select>
        {error && <div className="text-red-600">{error}</div>}
        <button type="submit" disabled={loading} className="bg-blue-600 text-white px-4 py-2 rounded">
          {loading ? "Predicting..." : "Predict"}
        </button>
      </form>
      {result && (
        <div className="mt-6">
          <PredictionResult prediction={result} />
        </div>
      )}
    </div>
  );
}
