import api from './api';

export const login = async (email, password) => {
  const response = await api.post('/auth/login', {
    username: email,  // Changed from email to username
    password
  });
  const { access_token, user } = response.data;
  localStorage.setItem('access_token', access_token);
  return user;
};

export const register = async (userData) => {
  // Add username field and map role correctly
  const payload = {
    ...userData,
    username: userData.email,
    role: userData.role === 'doctor' ? 'doctor' : 'patient'
  };
  
  const response = await api.post('/auth/register', payload);  // Fixed endpoint
  const { access_token, user } = response.data;
  localStorage.setItem('access_token', access_token);
  return user;
};

export const getCurrentUser = async () => {
  try {
    const response = await api.get('/auth/me');
    return response.data;
  } catch (error) {
    return null;
  }
};

export const getDemoCredentials = async () => {
  const response = await api.get('/auth/demo-credentials');
  return response.data;
};
