// src/components/chat/SessionSidebar.jsx
import React from "react";

const SessionSidebar = ({ sessions, currentSessionId, setSessionId }) => {
  return (
    <div className="w-64 bg-gray-100 border-r p-3 overflow-y-auto">
      <h2 className="text-xl font-semibold mb-3">Chat Sessions</h2>
      <ul>
        {sessions.map((sess) => (
          <li
            key={sess.session_id}
            onClick={() => setSessionId(sess.session_id)}
            className={`cursor-pointer p-2 rounded mb-1 ${
              sess.session_id === currentSessionId
                ? "bg-blue-500 text-white"
                : "hover:bg-blue-200"
            }`}
          >
            {sess.session_name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SessionSidebar;
