import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import MessageBubble from './MessageBubble';
import { sendChatMessage, getChatHistory, createNewChatSession } from '../../services/chat';
import { PaperAirplaneIcon, ArrowPathIcon } from '@heroicons/react/24/outline';
import PatientChatMenu from './PatientChatMenu';
import DoctorChatMenu from './DoctorChatMenu';

const ChatInterface = ({ sessionId }) => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const fetchHistory = async () => {
      if (sessionId) {
        setIsLoading(true);
        try {
          const history = await getChatHistory(sessionId);
          setMessages(Array.isArray(history) ? history : []);
        } catch (error) {
          console.error('Failed to load chat history:', error);
          setMessages([]);
        } finally {
          setIsLoading(false);
        }
      } else {
        setIsLoading(false);
        setMessages([]);
      }
    };

    fetchHistory();
  }, [sessionId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async (text = null) => {
    const messageToSend = text || inputMessage;
    if (!messageToSend.trim() || isSending) return;

    const userMessage = {
      message: messageToSend,
      session_id: sessionId,
      source: 'user',
      created_at: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    if (!text) setInputMessage('');
    
    setIsSending(true);
    try {
      const aiResponse = await sendChatMessage(messageToSend, sessionId);
      setMessages(prev => [
        ...prev,
        {
          message: aiResponse.response,
          session_id: sessionId,
          source: 'ai',
          created_at: new Date().toISOString(),
          personalized: aiResponse.personalized
        }
      ]);
    } catch (error) {
      console.error('Failed to send message:', error);
      setMessages(prev => [
        ...prev,
        {
          message: "I'm having trouble responding right now. Please try again later.",
          session_id: sessionId,
          source: 'ai',
          created_at: new Date().toISOString(),
          isError: true
        }
      ]);
    } finally {
      setIsSending(false);
    }
  };

  const handleNewSession = async () => {
    setIsLoading(true);
    try {
      const newSession = await createNewChatSession();
      setMessages([]);
      console.log('New session created:', newSession);
    } catch (error) {
      console.error('Failed to create new session:', error);
      setMessages([]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* Chat Header */}
      <div className="bg-gradient-to-r from-primary-600 to-primary-700 text-white p-4 rounded-t-xl">
        <div className="flex justify-between items-center">
          <h2 className="text-xl font-semibold">Cardio Health Assistant</h2>
          <button
            onClick={handleNewSession}
            className="flex items-center text-sm bg-white/20 hover:bg-white/30 px-3 py-1 rounded-lg transition-colors"
          >
            <ArrowPathIcon className="h-4 w-4 mr-1" />
            New Chat
          </button>
        </div>
      </div>

      {/* Messages Container */}
      <div className="flex-grow overflow-y-auto p-4 bg-gray-50">
        {isLoading ? (
          <div className="flex justify-center items-center h-full">
            <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-primary-500"></div>
          </div>
        ) : messages.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-center text-gray-500">
            <div className="bg-gray-200 border-2 border-dashed rounded-xl w-16 h-16 mb-4" />
            <h3 className="text-lg font-medium mb-2">Start a conversation</h3>
            <p className="max-w-md">
              {user?.role === 'doctor' 
                ? "Use the clinical tools below or type your medical query"
                : "Select a question below to get personalized advice about your cardiovascular health"}
            </p>
          </div>
        ) : (
          <div className="space-y-4">
            {messages.map((msg, index) => (
              <MessageBubble
                key={`${sessionId}-${index}`}
                message={msg}
                isUser={msg.source === 'user'}
              />
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Menu-Based Interface */}
      {user?.role === 'patient' && (
        <PatientChatMenu onSelectQuestion={handleSendMessage} />
      )}
      
      {user?.role === 'doctor' && (
        <DoctorChatMenu onSelectAction={handleSendMessage} />
      )}

      {/* Input Area (only for doctors) */}
      {user?.role === 'doctor' && (
        <div className="p-4 bg-white border-t">
          <div className="flex items-end space-x-2">
            <textarea
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={handleKeyPress}
              placeholder="Type your message here..."
              className="flex-grow px-4 py-3 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500 resize-none"
              rows={1}
              disabled={isSending}
            />
            <button
              onClick={() => handleSendMessage()}
              disabled={!inputMessage.trim() || isSending}
              className="p-3 bg-gradient-to-r from-primary-600 to-primary-500 text-white rounded-lg hover:from-primary-700 hover:to-primary-600 transition-all disabled:opacity-50"
            >
              <PaperAirplaneIcon className="h-5 w-5" />
            </button>
          </div>
          <p className="text-xs text-gray-500 mt-2">
            Clinical mode: Responses include technical medical guidance
          </p>
        </div>
      )}
      
      {/* Info for patients */}
      {user?.role === 'patient' && (
        <div className="p-3 bg-blue-50 border-t border-blue-100 text-center">
          <p className="text-xs text-blue-700">
            Responses are in simple, easy-to-understand language
          </p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
