import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { processBatchPrediction } from '../../services/predict';
import { DocumentArrowUpIcon } from '@heroicons/react/24/outline';

const FileUploader = ({ onSuccess }) => {
  
  const [file, setFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.type !== 'text/csv') {
        setError('Please upload a CSV file');
        return;
      }
      setFile(selectedFile);
      setError('');
    }
  };

  const handleSubmit = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setIsProcessing(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('file', file);
      
      const result = await processBatchPrediction(formData);
      onSuccess(result);
    } catch (err) {
      console.error('Batch processing failed:', err);
      setError('Failed to process file. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6">
      <div className="text-center mb-6">
        <DocumentArrowUpIcon className="h-12 w-12 mx-auto text-primary-500" />
        <h2 className="text-xl font-bold text-gray-800 mt-2">Batch Prediction</h2>
        <p className="text-gray-600 mt-1">
          Upload a CSV file with patient data to perform multiple predictions at once
        </p>
      </div>

      <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center mb-6">
        <input
          type="file"
          id="batch-upload"
          className="hidden"
          accept=".csv"
          onChange={handleFileChange}
          disabled={isProcessing}
        />
        <label
          htmlFor="batch-upload"
          className="cursor-pointer block"
        >
          <div className="space-y-2">
            <div className="bg-gray-200 border-2 border-dashed rounded-xl w-16 h-16 mx-auto" />
            <p className="text-sm text-gray-600">
              <span className="font-medium text-primary-600">Click to upload</span> or drag and drop
            </p>
            <p className="text-xs text-gray-500">CSV files only</p>
          </div>
        </label>
        
        {file && (
          <div className="mt-4 p-3 bg-gray-50 rounded-lg inline-block">
            <p className="text-sm font-medium text-gray-700">
              {file.name} <span className="text-gray-500">({(file.size / 1024).toFixed(1)} KB)</span>
            </p>
          </div>
        )}
      </div>

      {error && (
        <div className="mb-4 p-3 bg-danger-50 text-danger-700 rounded-lg">
          {error}
        </div>
      )}

      <div className="flex justify-between items-center">
        <div>
          <a 
            href="/sample-batch-template.csv" 
            download
            className="text-sm text-primary-600 hover:text-primary-800"
          >
            Download sample template
          </a>
        </div>
        <button
          onClick={handleSubmit}
          disabled={isProcessing || !file}
          className="px-6 py-2 bg-gradient-to-r from-primary-600 to-primary-500 text-white rounded-lg hover:from-primary-700 hover:to-primary-600 transition-all disabled:opacity-70"
        >
          {isProcessing ? 'Processing...' : 'Run Predictions'}
        </button>
      </div>
    </div>
  );
};

export default FileUploader;
