import React, { useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import FileUploader from '../components/batch/FileUploader';
import BatchResults from '../components/batch/BatchResults';
import { useAuth } from '../context/AuthContext';

const BatchPredict = () => {
  const [result, setResult] = useState(null);
  const { user, isAuthenticated } = useAuth();

  // Check if user is authenticated and has doctor role
  if (!isAuthenticated || user?.role !== 'doctor') {
    return (
      <DashboardLayout>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center py-12">
            <div className="bg-gray-200 border-2 border-dashed rounded-xl w-16 h-16 mx-auto" />
            <h3 className="mt-4 text-xl font-medium text-gray-900">Access Denied</h3>
            <p className="mt-2 text-gray-500">
              This feature is only available for doctors.
            </p>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Batch Prediction</h1>
          <p className="mt-2 text-lg text-gray-600">
            Upload a CSV file to predict cardiovascular risk for multiple patients at once
          </p>
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div>
            <FileUploader onSuccess={setResult} />
          </div>
          <div>
            {result ? (
              <BatchResults result={result} />
            ) : (
              <div className="bg-white rounded-xl shadow-lg p-8 text-center">
                <div className="bg-gray-200 border-2 border-dashed rounded-xl w-16 h-16 mx-auto" />
                <h3 className="mt-4 text-xl font-medium text-gray-900">No results yet</h3>
                <p className="mt-2 text-gray-500">
                  Upload a CSV file to see batch prediction results
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default BatchPredict;
