// src/components/auth/RegisterForm.jsx
import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Link as MuiLink,
  CircularProgress,
  MenuItem,
  FormControl,
  InputLabel,
  Select
} from '@mui/material';
import { styled } from '@mui/system';

const FormContainer = styled(Container)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  minHeight: '100vh',
  background: 'linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)',
  color: '#fff',
}));

const StyledBox = styled(Box)(({ theme }) => ({
  background: '#fff',
  padding: theme.spacing(4),
  borderRadius: theme.shape.borderRadius,
  boxShadow: theme.shadows[5], // Ensure theme.shadows[5] is available via ThemeProvider in App.jsx
  width: '100%',
  maxWidth: '400px',
  display: 'flex',
  flexDirection: 'column',
  gap: theme.spacing(2),
}));

const StyledTextField = styled(TextField)({
  '& .MuiInputBase-input': {
    color: '#333',
  },
  '& .MuiInputLabel-root': {
    color: '#666',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#bbb',
    },
    '&:hover fieldset': {
      borderColor: '#6a11cb',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#2575fc',
    },
  },
});

const GradientButton = styled(Button)(({ theme }) => ({
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
  border: 0,
  borderRadius: 3,
  boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  color: 'white',
  height: 48,
  padding: '0 30px',
  marginTop: theme.spacing(2),
}));

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    full_name: '',
    password: '',
    confirmPassword: '',
    role: 'patient', // Default role
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({}); // State to hold validation errors
  const { register } = useAuth();
  const navigate = useNavigate();

  // Robust email validation regex
  const validateEmail = (email) => {
    return String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      );
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // Clear error for the field being changed
    if (errors[name]) {
      setErrors((prevErrors) => ({ ...prevErrors, [name]: '' }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({}); // Clear previous errors

    let newErrors = {};

    // Validate email
    if (!formData.email) {
      newErrors.email = 'Email is required.';
    } else if (!validateEmail(formData.email)) {
      newErrors.email = 'Invalid email format.';
    } else if (formData.email.includes("@example.com")) { // Client-side check for backend specific rule
      newErrors.email = 'Email addresses from example.com are not allowed.';
    }

    // Validate full_name
    if (!formData.full_name) {
      newErrors.full_name = 'Full name is required.';
    } else if (formData.full_name.length < 3) { // <-- Client-side validation for min_length=3
      newErrors.full_name = 'Full name must be at least 3 characters.';
    }

    // Validate password
    if (!formData.password) {
      newErrors.password = 'Password is required.';
    } else if (formData.password.length < 8) { // <-- Client-side validation for min_length=8
      newErrors.password = 'Password must be at least 8 characters.';
    }

    // Validate confirmPassword
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match.';
    }

    // If any validation errors, set them and stop submission
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setLoading(false);
      return;
    }

    // Debugging: Log the data being sent
    console.log("Attempting to register with data:", {
      email: formData.email,
      full_name: formData.full_name,
      password: formData.password,
      role: formData.role,
    });

    try {
      await register({
        email: formData.email,
        full_name: formData.full_name,
        password: formData.password,
        role: formData.role,
      });
      navigate('/dashboard'); // Redirect on successful registration
    } catch (error) {
      console.error('Registration failed:', error);
      // Handle specific error messages from backend if available
      let errorMessage = 'Registration failed. Please try again.';
      if (error.response && error.response.data && error.response.data.detail) {
        if (typeof error.response.data.detail === 'string') {
          errorMessage = error.response.data.detail;
        } else if (Array.isArray(error.response.data.detail) && error.response.data.detail.length > 0) {
          // For Pydantic validation errors, take the first message
          errorMessage = error.response.data.detail[0].msg || errorMessage;
          // You could also iterate and show all messages if desired
        } else if (error.response.data.detail.detail) { // Sometimes nested detail
          errorMessage = error.response.data.detail.detail;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }
      setErrors({ api: errorMessage });
    } finally {
      setLoading(false);
    }
  };

  return (
    <FormContainer maxWidth="sm">
      <StyledBox>
        <Typography variant="h4" component="h1" gutterBottom align="center" color="primary">
          Register
        </Typography>
        <form onSubmit={handleSubmit}>
          <StyledTextField
            label="Email"
            variant="outlined"
            fullWidth
            margin="normal"
            name="email"
            value={formData.email}
            onChange={handleChange}
            error={!!errors.email}
            helperText={errors.email}
            type="email"
            required
          />
          <StyledTextField
            label="Full Name"
            variant="outlined"
            fullWidth
            margin="normal"
            name="full_name"
            value={formData.full_name}
            onChange={handleChange}
            error={!!errors.full_name}
            helperText={errors.full_name}
            required
          />
          <StyledTextField
            label="Password (min 8 characters)"
            variant="outlined"
            fullWidth
            margin="normal"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
            error={!!errors.password}
            helperText={errors.password}
            required
          />
          <StyledTextField
            label="Confirm Password"
            variant="outlined"
            fullWidth
            margin="normal"
            name="confirmPassword"
            type="password"
            value={formData.confirmPassword}
            onChange={handleChange}
            error={!!errors.confirmPassword}
            helperText={errors.confirmPassword}
            required
          />
          <FormControl fullWidth margin="normal" error={!!errors.role}>
            <InputLabel id="role-select-label">Role</InputLabel>
            <Select
              labelId="role-select-label"
              id="role-select"
              name="role"
              value={formData.role}
              label="Role"
              onChange={handleChange}
            >
              <MenuItem value="patient">Patient</MenuItem>
              <MenuItem value="doctor">Doctor</MenuItem>
            </Select>
            {errors.role && <Typography color="error" variant="caption">{errors.role}</Typography>}
          </FormControl>
          {errors.api && (
            <Typography color="error" align="center" variant="body2">
              {errors.api}
            </Typography>
          )}
          <GradientButton
            type="submit"
            fullWidth
            variant="contained"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Register'}
          </GradientButton>
        </form>
        <Typography variant="body2" align="center" sx={{ mt: 2, color: '#666' }}>
          Already have an account?{' '}
          <MuiLink href="/login" sx={{ color: '#2575fc', textDecoration: 'none' }}>
            Login
          </MuiLink>
        </Typography>
      </StyledBox>
    </FormContainer>
  );
};

export default RegisterForm;
