// src/utils/helpers.js

export function getRiskCategoryInfo(category) {
  // Adjust categories and info as per your app's structure
  const categories = {
    "Low": {
      color: "green",
      label: "Low Risk",
      description: "You are at a low risk for CVD. Keep maintaining a healthy lifestyle."
    },
    "Moderate": {
      color: "yellow",
      label: "Moderate Risk",
      description: "Your risk is moderate. Consider lifestyle improvements and regular checkups."
    },
    "High": {
      color: "red",
      label: "High Risk",
      description: "High risk detected. Consult your doctor and take preventive actions immediately."
    }
  };
  // Default in case category not matched
  return categories[category] || {
    color: "gray",
    label: "Unknown",
    description: "Risk category information not available."
  };
}

export function getAuthToken() {
  const userStr = localStorage.getItem("user");
  if (!userStr) return null;
  try {
    const user = JSON.parse(userStr);
    return user?.token || null;
  } catch {
    return null;
  }
}

export function saveUserToStorage(user) {
  localStorage.setItem("user", JSON.stringify(user));
}

export function clearUserFromStorage() {
  localStorage.removeItem("user");
}
