import React, { useEffect, useState } from "react";
import useAuth from "../hooks/useAuth";
import StatsCard from "../components/dashboard/StatsCard";
import RiskGauge from "../components/dashboard/RiskGauge";
import PredictionHistory from "../components/dashboard/PredictionHistory";
import ActivityTimeline from "../components/dashboard/ActivityTimeline";
import BatchResults from "../components/batch/BatchResults";
import FileUploader from "../components/batch/FileUploader";
import ChatInterface from "../components/chat/ChatInterface";
import DashboardLayout from "../layouts/DashboardLayout";
import { fetchUserDashboard, fetchUserActivities, fetchDoctorActivities } from "../services/dashboard";

export default function Dashboard() {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [prediction, setPrediction] = useState(null);
  const [activities, setActivities] = useState([]);
  const [batchResults, setBatchResults] = useState([]);

  useEffect(() => {
    if (!user) return;
    const fetchAll = async () => {
      setLoading(true);
      try {
        if (user.role === "user") {
          const dash = await fetchUserDashboard(user.id);
          const acts = await fetchUserActivities(user.id);
          setPrediction(dash);
          setActivities(acts);
        } else if (user.role === "doctor") {
          const acts = await fetchDoctorActivities(user.id);
          setActivities(acts);
        }
      } catch (err) {
        // Optionally handle error for dashboard load
      } finally {
        setLoading(false);
      }
    };
    fetchAll();
  }, [user]);

  if (loading) return <DashboardLayout><div className="text-center mt-20 text-lg">Loading dashboard...</div></DashboardLayout>;

  const handleBatchResults = results => setBatchResults(results);

  return (
    <DashboardLayout>
      <h1 className="text-3xl font-bold mb-6">
        Welcome, {user?.role === "doctor" ? `Dr. ${user.full_name}` : user?.full_name}
      </h1>
      {user.role === "user" && (
        <>
          <StatsCard prediction={prediction} />
          <RiskGauge risk={prediction?.risk_percentage} />
          <PredictionHistory history={activities} />
          <ActivityTimeline activities={activities?.activities} />
          <ChatInterface role="user" />
        </>
      )}
      {user.role === "doctor" && (
        <>
          <FileUploader onResults={handleBatchResults} />
          {batchResults.length > 0 && <BatchResults results={batchResults} />}
          <ActivityTimeline activities={activities} />
          <ChatInterface role="doctor" />
        </>
      )}
    </DashboardLayout>
  );
}
