// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { ChatProvider } from './context/ChatContext';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import Predict from './pages/Predict';
import BatchPredict from './pages/BatchPredict';
import Chat from './pages/Chat';
import Profile from './pages/Profile';
import ProtectedRoute from './components/common/ProtectedRoute';

// Import ThemeProvider and createTheme for Material-UI styling
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline'; // Optional: for consistent baseline styling

// Define a basic Material-UI theme
// The default theme includes 'shadows', so we just need to create it.
const theme = createTheme({
  // You can customize your theme here if needed
  // For example:
  // palette: {
  //   primary: {
  //     main: '#6a11cb',
  //   },
  //   secondary: {
  //     main: '#2575fc',
  //   },
  // },
  // typography: {
  //   fontFamily: 'Roboto, sans-serif',
  // },
});

function App() {
  return (
    // Wrap your entire application with ThemeProvider
    <ThemeProvider theme={theme}>
      {/* CssBaseline provides a consistent baseline to build upon. */}
      <CssBaseline />
      <Router>
        <AuthProvider>
          <ChatProvider>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/dashboard" element={
                <ProtectedRoute allowedRoles={['patient', 'doctor']}>
                  <Dashboard />
                </ProtectedRoute>
              } />
              <Route path="/predict" element={
                <ProtectedRoute allowedRoles={['patient', 'doctor']}>
                  <Predict />
                </ProtectedRoute>
              } />
              <Route path="/batch-predict" element={
                <ProtectedRoute allowedRoles={['doctor']}>
                  <BatchPredict />
                </ProtectedRoute>
              } />
              <Route path="/chat" element={
                <ProtectedRoute allowedRoles={['patient', 'doctor']}>
                  <Chat />
                </ProtectedRoute>
              } />
              <Route path="/profile" element={
                <ProtectedRoute allowedRoles={['patient', 'doctor']}>
                  <Profile />
                </ProtectedRoute>
              } />
              {/* Add a catch-all route for 404 if desired */}
              {/* <Route path="*" element={<NotFoundPage />} /> */}
            </Routes>
          </ChatProvider>
        </AuthProvider>
      </Router>
    </ThemeProvider>
  );
}

export default App;
