// src/components/auth/LoginForm.jsx
import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Link as MuiLink,
  CircularProgress,
} from '@mui/material';
import { styled } from '@mui/system';

const FormContainer = styled(Container)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  minHeight: '100vh',
  background: 'linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)',
  color: '#fff',
}));

const StyledBox = styled(Box)(({ theme }) => ({
  background: '#fff',
  padding: theme.spacing(4),
  borderRadius: theme.shape.borderRadius,
  boxShadow: theme.shadows[5], // Ensure theme.shadows[5] is available via ThemeProvider in App.jsx
  width: '100%',
  maxWidth: '400px',
  display: 'flex',
  flexDirection: 'column',
  gap: theme.spacing(2),
}));

const StyledTextField = styled(TextField)({
  '& .MuiInputBase-input': {
    color: '#333',
  },
  '& .MuiInputLabel-root': {
    color: '#666',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#bbb',
    },
    '&:hover fieldset': {
      borderColor: '#6a11cb',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#2575fc',
    },
  },
});

const GradientButton = styled(Button)(({ theme }) => ({
  background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
  border: 0,
  borderRadius: 3,
  boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  color: 'white',
  height: 48,
  padding: '0 30px',
  marginTop: theme.spacing(2),
}));

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({}); // State to hold validation errors
  const { login } = useAuth();
  const navigate = useNavigate();

  // Robust email validation regex
  const validateEmail = (email) => {
    return String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({}); // Clear previous errors

    let newErrors = {};

    // Validate email
    if (!email) {
      newErrors.email = 'Email is required.';
    } else if (!validateEmail(email)) {
      newErrors.email = 'Invalid email format.';
    }

    // Validate password
    if (!password) {
      newErrors.password = 'Password is required.';
    }

    // If any validation errors, set them and stop submission
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setLoading(false);
      return;
    }

    try {
      await login(email, password); // Assuming useAuth's login takes email and password separately
      navigate('/dashboard'); // Redirect on successful login
    } catch (error) {
      console.error('Login failed:', error);
      let errorMessage = 'Login failed. Please check your credentials.';
      if (error.response && error.response.data && error.response.data.detail) {
        if (typeof error.response.data.detail === 'string') {
          errorMessage = error.response.data.detail;
        } else if (error.response.data.detail.detail) { // Sometimes nested detail
          errorMessage = error.response.data.detail.detail;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }
      setErrors({ api: errorMessage });
    } finally {
      setLoading(false);
    }
  };

  return (
    <FormContainer maxWidth="sm">
      <StyledBox>
        <Typography variant="h4" component="h1" gutterBottom align="center" color="primary">
          Login
        </Typography>
        <form onSubmit={handleSubmit}>
          <StyledTextField
            label="Email"
            variant="outlined"
            fullWidth
            margin="normal"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
              if (errors.email) setErrors((prevErrors) => ({ ...prevErrors, email: '' }));
            }}
            error={!!errors.email}
            helperText={errors.email}
            type="email"
            required
          />
          <StyledTextField
            label="Password"
            variant="outlined"
            fullWidth
            margin="normal"
            type="password"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
              if (errors.password) setErrors((prevErrors) => ({ ...prevErrors, password: '' }));
            }}
            error={!!errors.password}
            helperText={errors.password}
            required
          />
          {errors.api && (
            <Typography color="error" align="center" variant="body2">
              {errors.api}
            </Typography>
          )}
          <GradientButton
            type="submit"
            fullWidth
            variant="contained"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Login'}
          </GradientButton>
        </form>
        <Typography variant="body2" align="center" sx={{ mt: 2, color: '#666' }}>
          Don't have an account?{' '}
          <MuiLink href="/register" sx={{ color: '#2575fc', textDecoration: 'none' }}>
            Register
          </MuiLink>
        </Typography>
      </StyledBox>
    </FormContainer>
  );
};

export default LoginForm;
