export function getAuthToken() {
  const userStr = localStorage.getItem("user");
  if (!userStr) return null;
  try {
    const user = JSON.parse(userStr);
    return user?.token || null;
  } catch {
    return null;
  }
}
export function saveUserToStorage(user) {
  localStorage.setItem("user", JSON.stringify(user));
}
export function clearUserFromStorage() {
  localStorage.removeItem("user");
}
export function getRiskCategoryInfo(category) {
  const categories = {
    Low: {
      color: "green",
      label: "Low Risk",
      description: "You are at low risk for CVD. Maintain healthy habits.",
    },
    Moderate: {
      color: "yellow",
      label: "Moderate Risk",
      description: "Moderate risk. Improve your lifestyle and check with your doctor.",
    },
    High: {
      color: "red",
      label: "High Risk",
      description: "High risk! Immediate medical consult is advised.",
    }
  };
  return categories[category] || {
    color: "gray",
    label: "Unknown",
    description: "No information available.",
  };
}
