import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { registerUser } from "../../services/auth";

const RegisterForm = () => {
  const [values, setValues] = useState({
    full_name: "", email: "", password: "", role: "user"
  });
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => {
    const { name, value } = e.target;
    setValues(v => ({ ...v, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError("");
    setSubmitting(true);
    try {
      await registerUser(values);
      navigate("/login");
    } catch (err) {
      setError(err.response?.data?.detail || "Registration failed");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow" onSubmit={handleSubmit}>
      <h2 className="text-2xl mb-6 font-semibold text-center">Create Account</h2>
      {error && <p className="mb-4 text-red-600">{error}</p>}
      <div className="mb-4">
        <label className="block mb-1 font-medium">Full Name</label>
        <input type="text" name="full_name" value={values.full_name} onChange={handleChange}
          className="w-full border rounded px-3 py-2" required autoFocus />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium">Email</label>
        <input type="email" name="email" value={values.email} onChange={handleChange}
          className="w-full border rounded px-3 py-2" required />
      </div>
      <div className="mb-4">
        <label className="block mb-1 font-medium">Password</label>
        <input type="password" name="password" value={values.password} onChange={handleChange}
          className="w-full border rounded px-3 py-2" required />
      </div>
      <div className="mb-6">
        <label className="block mb-1 font-medium">Role</label>
        <select name="role" className="w-full border rounded px-3 py-2" value={values.role} onChange={handleChange}>
          <option value="user">Patient</option>
          <option value="doctor">Doctor</option>
        </select>
      </div>
      <button type="submit" disabled={submitting} className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition">
        {submitting ? "Registering…" : "Register"}
      </button>
      <p className="mt-4 text-center text-sm">
        Already have an account? <Link to="/login" className="text-green-600 hover:underline">Sign in</Link>
      </p>
    </form>
  );
};

export default RegisterForm;
