// src/components/chat/PatientChatMenu.jsx
import React from 'react';

const PatientChatMenu = ({ onSelectQuestion }) => {
  const categories = [
    {
      title: "Symptoms",
      questions: [
        "What are common heart attack symptoms?",
        "What does chest pain feel like?",
        "When should I worry about shortness of breath?"
      ]
    },
    {
      title: "Diet & Nutrition",
      questions: [
        "What foods are good for heart health?",
        "How much sodium should I consume?",
        "Is red wine good for my heart?"
      ]
    },
    {
      title: "Exercise",
      questions: [
        "What exercises are safe for my heart?",
        "How often should I exercise?",
        "What are good exercises for high blood pressure?"
      ]
    },
    {
      title: "Medications",
      questions: [
        "What are common side effects of blood pressure meds?",
        "Should I take aspirin daily?",
        "How do statins work?"
      ]
    }
  ];

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mb-4">
      <h3 className="text-lg font-medium text-gray-900 mb-3">What would you like to know?</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {categories.map((category, catIndex) => (
          <div key={catIndex} className="border border-gray-200 rounded-lg p-3">
            <h4 className="font-medium text-gray-800 mb-2">{category.title}</h4>
            <div className="space-y-2">
              {category.questions.map((question, qIndex) => (
                <button
                  key={qIndex}
                  onClick={() => onSelectQuestion(question)}
                  className="w-full text-left px-3 py-2 bg-gray-50 hover:bg-gray-100 rounded-md text-sm text-gray-700 transition-colors"
                >
                  {question}
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
      <div className="mt-4 p-3 bg-blue-50 border border-blue-100 rounded-lg">
        <p className="text-sm text-blue-700">
          <span className="font-medium">Emergency:</span> If you're experiencing chest pain,
          shortness of breath, or dizziness, call emergency services immediately.
        </p>
      </div>
    </div>
  );
};

export default PatientChatMenu;
