// src/components/dashboard/RiskGauge.jsx
import React from "react";

const RiskGauge = ({ risk }) => {
  if (risk == null) return null;

  let color = "bg-green-500";
  if (risk >= 20 && risk < 40) color = "bg-yellow-400";
  else if (risk >= 40) color = "bg-red-600";

  return (
    <div className="my-6">
      <h3 className="mb-2 font-semibold text-lg">Risk Gauge</h3>
      <div className="w-full h-6 bg-gray-200 rounded-full overflow-hidden">
        <div className={`${color} h-6`} style={{ width: `${risk}%` }} />
      </div>
    </div>
  );
};

export default RiskGauge;
