import React, { useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import ChatInterface from '../components/chat/ChatInterface';
import SessionSidebar from '../components/chat/SessionSidebar';
import { useAppContext } from '../../context/AppContext';

const Chat = () => {
  const [activeSession, setActiveSession] = useState('1');
  const { currentChat, setCurrentChat, resetChat } = useAppContext();

  const sendMessage = async (message) => {
    const newMessage = {
      id: Date.now(),
      content: message,
      sender: 'user',
      timestamp: new Date().toISOString()
    };
    
    // Update context
    setCurrentChat([...currentChat, newMessage]);
    
    // Simulate AI response
    setTimeout(() => {
      const aiResponse = {
        id: Date.now() + 1,
        content: "I'm processing your request...",
        sender: 'ai',
        timestamp: new Date().toISOString()
      };
      setCurrentChat(prev => [...prev, aiResponse]);
    }, 1000);
  };

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Health Assistant</h1>
            <p className="mt-2 text-lg text-gray-600">
              Get personalized advice about your cardiovascular health
            </p>
          </div>
          <button
            onClick={resetChat}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
          >
            New Chat
          </button>
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          <div className="lg:col-span-1">
            <SessionSidebar
              activeSession={activeSession}
              setActiveSession={setActiveSession}
            />
          </div>
          <div className="lg:col-span-3">
            <ChatInterface 
              messages={currentChat} 
              onSend={sendMessage} 
              sessionId={activeSession}
            />
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Chat;
