// src/components/dashboard/ActivityTimeline.jsx
import React from "react";

const ActivityTimeline = ({ activities }) => {
  if (!activities || activities.length === 0) return <p>No activities to display.</p>;

  return (
    <div className="mb-6 p-4 bg-white rounded shadow">
      <h3 className="mb-3 font-semibold text-lg">Activities</h3>
      <ul className="space-y-2 max-h-48 overflow-y-auto">
        {activities.map((act) => (
          <li key={act.id || act.session_id} className="border-b py-2">
            <p>{act.description || act.session_name || act.activity}</p>
            <p className="text-sm text-gray-500">{new Date(act.created_at || act.timestamp).toLocaleString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ActivityTimeline;
