import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';
import { useAppContext } from './AppContext';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('access_token'));
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { logout: appLogout } = useAppContext();

  useEffect(() => {
    const fetchUser = async () => {
      if (!token) {
        setLoading(false);
        return;
      }

      try {
        const res = await api.get('/auth/me');
        setUser(res.data);
      } catch (err) {
        console.error('âŒ Failed to fetch user:', err);
        setUser(null);
        localStorage.removeItem('access_token');
        setToken(null);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [token]);

  const login = async (email, password) => {
    try {
      console.log('ðŸ” LOGIN DEBUG - Input:', { email, password: '***' });
      
      const loginData = {
        email: email,
        password: password
      };
      
      console.log('ðŸ” LOGIN DEBUG - Sending JSON:', { 
        email: loginData.email, 
        password: '***' 
      });

      const res = await api.post('/auth/login', loginData, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('âœ… LOGIN SUCCESS:', res.data);

      const accessToken = res.data.access_token;
      
      if (!accessToken) {
        throw new Error('No access_token in response');
      }

      localStorage.setItem('access_token', accessToken);
      setToken(accessToken);

      const userRes = await api.get('/auth/me');
      setUser(userRes.data);
      
      return userRes.data;
      
    } catch (error) {
      console.error('âŒ LOGIN ERROR:', error);
      console.error('âŒ LOGIN ERROR - Response:', error.response?.data);
      console.error('âŒ LOGIN ERROR - Status:', error.response?.status);
      throw error;
    }
  };

  const register = async (userData) => {
    const res = await api.post('/auth/register', userData);
    return res.data;
  };

  const logout = () => {
    // Call app logout to clear session data
    appLogout();
    
    // Clear auth-specific data
    localStorage.removeItem('access_token');
    setToken(null);
    setUser(null);
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      login, 
      register, 
      logout, 
      loading, 
      isAuthenticated: !!user 
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
