import axios from "../api/axios";

// Fetch user dashboard
export async function fetchUserDashboard(id) {
  // all single prediction data
  const res = await axios.get(`/prediction/user/${id}`);
  return res.data;
}

export async function fetchUserActivities(id) {
  const res = await axios.get(`/dashboard/activities/user/${id}`);
  return res.data;
}

// Doctor patient activities
export async function fetchDoctorActivities(id) {
  const res = await axios.get(`/dashboard/activities/doctor/${id}`);
  return res.data;
}
