import api from './api';

// Send a message to the chat API
export const sendChatMessage = async (message, sessionId = null) => {
  try {
    console.log('ðŸ“¤ Sending chat message:', { message, sessionId });
    
    // Try different request formats that backends commonly expect
    const requestFormats = [
      // Format 1: Simple message object
      {
        message: message,
        session_id: sessionId
      },
      // Format 2: Content field
      {
        content: message,
        session_id: sessionId
      },
      // Format 3: Text field
      {
        text: message,
        session_id: sessionId
      },
      // Format 4: Query field (common for AI endpoints)
      {
        query: message,
        session_id: sessionId
      }
    ];

    let lastError = null;

    // Try each format until one works
    for (let i = 0; i < requestFormats.length; i++) {
      try {
        console.log(`ðŸ“¤ Trying request format ${i + 1}:`, requestFormats[i]);
        
        const response = await api.post('/chat/message', requestFormats[i]);
        
        console.log('âœ… Chat API success with format', i + 1, ':', response.data);
        
        // Normalize the response format
        return {
          id: response.data.id || Date.now(),
          content: response.data.content || response.data.message || response.data.response || response.data.text,
          sender: 'ai',
          timestamp: response.data.timestamp || new Date().toISOString()
        };
        
      } catch (error) {
        console.log(`âŒ Format ${i + 1} failed:`, error.response?.status, error.response?.data);
        lastError = error;
        continue;
      }
    }

    // If all formats failed, throw the last error
    throw lastError;

  } catch (error) {
    console.error('Chat API error:', {
      status: error.response?.status,
      data: error.response?.data,
      message: error.message
    });

    // Provide helpful error messages based on status
    if (error.response?.status === 422) {
      const detail = error.response?.data?.detail;
      if (Array.isArray(detail)) {
        const fieldErrors = detail.map(err => `${err.loc?.join('.')} - ${err.msg}`).join(', ');
        throw new Error(`Invalid request format: ${fieldErrors}`);
      } else {
        throw new Error(`Invalid request format: ${detail || 'Please check the message format'}`);
      }
    } else if (error.response?.status === 401) {
      throw new Error('Authentication required - please login');
    } else if (error.response?.status === 500) {
      throw new Error('Server error - please try again later');
    } else if (!error.response) {
      throw new Error('Network error - check if backend is running');
    } else {
      throw new Error(`Chat service error: ${error.message}`);
    }
  }
};

// Get chat sessions (if needed)
export const getChatSessions = async () => {
  try {
    const response = await api.get('/chat/sessions');
    return response.data;
  } catch (error) {
    console.error('Failed to get chat sessions:', error);
    throw error;
  }
};

// Create a new chat session (if needed)
export const createChatSession = async (title = 'New Chat') => {
  try {
    const response = await api.post('/chat/sessions', { title });
    return response.data;
  } catch (error) {
    console.error('Failed to create chat session:', error);
    throw error;
  }
};
