import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import DashboardLayout from '../layouts/DashboardLayout';
import StatsCard from '../components/dashboard/StatsCard';
import RiskGauge from '../components/dashboard/RiskGauge';
import PredictionHistory from '../components/dashboard/PredictionHistory';
import ActivityTimeline from '../components/dashboard/ActivityTimeline';
import DashboardHeader from '../components/dashboard/DashboardHeader';
import { getDashboardData } from '../services/dashboard';
import LoadingSpinner from '../components/common/LoadingSpinner';

const Dashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  // Debug localStorage and login status
  useEffect(() => {
    console.log('ðŸ” DASHBOARD DEBUG:');
    console.log('- All localStorage keys:', Object.keys(localStorage));
    console.log('- access_token value:', localStorage.getItem('access_token'));
    console.log('- User object:', user);
    console.log('- User role:', user?.role);
    
    // Test manual login debug
    const testLoginResponse = async () => {
      try {
        console.log('ðŸ” Testing what login endpoint returns...');
        const formData = new URLSearchParams();
        formData.append('username', 'test@example.com'); // Replace with actual test credentials
        formData.append('password', 'testpassword');     // Replace with actual test credentials
        
        const response = await fetch('http://localhost:8000/auth/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
          body: formData,
          credentials: 'include'
        });
        
        if (response.ok) {
          const data = await response.json();
          console.log('ðŸ” RAW LOGIN RESPONSE:', data);
          console.log('ðŸ” AVAILABLE FIELDS:', Object.keys(data));
          console.log('ðŸ” HAS access_token?', 'access_token' in data);
          console.log('ðŸ” HAS token?', 'token' in data);
        }
      } catch (error) {
        console.log('ðŸ” Login test failed (expected):', error.message);
      }
    };
    
    // Only run once
    if (!window.debugRunOnce) {
      window.debugRunOnce = true;
      testLoginResponse();
    }
  }, [user]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('ðŸ“Š Fetching dashboard data via service...');
        const data = await getDashboardData(user.role);
        console.log("ðŸ“Š dashboardData:", data);
        setDashboardData(data);
      } catch (error) {
        console.error('Failed to fetch dashboard data:', {
          message: error.message,
          status: error.response?.status,
          data: error.response?.data,
          url: error.config?.url
        });
      } finally {
        setLoading(false);
      }
    };

    if (user?.role) {
      fetchData();
    }
  }, [user.role]);

  if (loading || !dashboardData) {
    return (
      <DashboardLayout>
        <div className="flex justify-center items-center h-64">
          <LoadingSpinner />
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <DashboardHeader />

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatsCard
            title="Total Predictions"
            value={dashboardData?.statistics?.total_predictions || 0}
            trend={12}
            icon="chartBar"
          />
          <StatsCard
            title="Recent Activity"
            value={dashboardData?.statistics?.recent_predictions || 0}
            trend={5}
            icon="clock"
          />
          {user?.role === 'doctor' ? (
            <StatsCard
              title="Total Patients"
              value={dashboardData?.statistics?.total_patients || 0}
              trend={3}
              icon="userGroup"
            />
          ) : (
            <StatsCard
              title="Current Risk"
              value={dashboardData?.statistics?.risk_distribution?.High || 0}
              trend={-2}
              icon="exclamation"
              isRisk={true}
            />
          )}
          <StatsCard
            title="High Risk"
            value={dashboardData?.statistics?.risk_distribution?.High || 0}
            trend={8}
            icon="shieldExclamation"
            isRisk={true}
          />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          <div className="lg:col-span-1 bg-white rounded-xl shadow-md p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Your CVD Risk Level</h2>
            <RiskGauge
              value={dashboardData?.latest_prediction?.risk_percentage || 0}
              category={dashboardData?.latest_prediction?.risk_category || 'Low'}
            />
            <div className="mt-4 text-sm text-gray-600">
              {dashboardData?.latest_prediction?.risk_category === 'High' ? (
                <p>Consult with your doctor immediately about cardiovascular health strategies.</p>
              ) : dashboardData?.latest_prediction?.risk_category === 'Moderate' ? (
                <p>Consider lifestyle changes to reduce your cardiovascular risk.</p>
              ) : (
                <p>Maintain your healthy lifestyle to keep your risk low.</p>
              )}
            </div>
          </div>

          <div className="lg:col-span-2 bg-white rounded-xl shadow-md p-6">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Prediction History</h2>
            <PredictionHistory predictions={dashboardData?.recent_predictions || []} />
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Recent Activity</h2>
          <ActivityTimeline activities={dashboardData?.recent_activities || []} />
        </div>
      </div>
    </DashboardLayout>
  );
};

export default Dashboard;
