import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import DashboardLayout from '../layouts/DashboardLayout';
import StatsCard from '../components/dashboard/StatsCard';
import RiskGauge from '../components/dashboard/RiskGauge';
import PredictionHistory from '../components/dashboard/PredictionHistory';
import ActivityTimeline from '../components/dashboard/ActivityTimeline';
import DashboardHeader from '../components/dashboard/DashboardHeader';
import { getDashboardData } from '../services/dashboard';
import LoadingSpinner from '../components/common/LoadingSpinner';
import { AlertTriangle, RefreshCw, TrendingUp, Users, Activity, Heart } from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedStatsDetail, setSelectedStatsDetail] = useState(null);
  const [retryCount, setRetryCount] = useState(0);

  // Enhanced mock data with proper structure
  const getMockData = () => ({
    user_info: {
      id: user?.id || 1,
      full_name: user?.full_name || user?.name || "Demo User",
      email: user?.email || "demo@example.com",
      role: user?.role || "patient",
      last_login: new Date().toISOString()
    },
    statistics: {
      total_predictions: user?.role === 'doctor' ? 156 : 8,
      recent_predictions: user?.role === 'doctor' ? 23 : 3,
      recent_activity: user?.role === 'doctor' ? 45 : 5,
      total_patients: user?.role === 'doctor' ? 89 : null,
      risk_distribution: {
        High: user?.role === 'doctor' ? 23 : 2,
        Moderate: user?.role === 'doctor' ? 45 : 3,
        Low: user?.role === 'doctor' ? 88 : 3
      }
    },
    latest_prediction: {
      id: 1,
      risk_percentage: user?.role === 'doctor' ? 45 : 35,
      risk_category: user?.role === 'doctor' ? 'Moderate' : 'Low',
      created_at: new Date().toISOString(),
      features: {
        age: 45,
        sex: 1,
        cigsPerDay: 0,
        totChol: 220,
        sysBP: 130,
        diaBP: 85,
        glucose: 95
      }
    },
    recent_predictions: [
      {
        id: 1,
        risk_percentage: 35,
        risk_category: 'Low',
        created_at: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
        features: { age: 45, sex: 1, cigsPerDay: 0, totChol: 220, sysBP: 130, diaBP: 85, glucose: 95 }
      },
      {
        id: 2,
        risk_percentage: 42,
        risk_category: 'Moderate',
        created_at: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000).toISOString(),
        features: { age: 45, sex: 1, cigsPerDay: 5, totChol: 240, sysBP: 140, diaBP: 90, glucose: 105 }
      },
      {
        id: 3,
        risk_percentage: 28,
        risk_category: 'Low',
        created_at: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
        features: { age: 45, sex: 1, cigsPerDay: 0, totChol: 200, sysBP: 120, diaBP: 80, glucose: 90 }
      }
    ],
    recent_activities: [
      {
        id: 1,
        description: user?.role === 'doctor' ? "Patient risk assessment completed" : "CVD risk prediction completed",
        target: "35% risk level",
        date: new Date().toISOString()
      },
      {
        id: 2,
        description: user?.role === 'doctor' ? "New patient registered" : "Health profile updated",
        target: user?.role === 'doctor' ? "John Smith added" : "Profile synced",
        date: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString()
      },
      {
        id: 3,
        description: user?.role === 'doctor' ? "Batch analysis completed" : "Previous prediction reviewed",
        target: user?.role === 'doctor' ? "25 patients analyzed" : "Historical data",
        date: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000).toISOString()
      }
    ]
  });

  useEffect(() => {
    fetchData();
  }, [user?.role]);

  const fetchData = async () => {
    if (!user?.role) return;
    
    try {
      setLoading(true);
      setError(null);
      console.log('📊 Fetching dashboard data via service...');
      const data = await getDashboardData(user.role);
      console.log("📊 dashboardData:", data);
      setDashboardData(data);
      setRetryCount(0);
    } catch (error) {
      console.error('Failed to fetch dashboard data:', {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data,
        url: error.config?.url
      });
      
      setError(`Unable to load live data${error.response?.status ? ` (Error ${error.response.status})` : ''}. Showing demo dashboard.`);
      setDashboardData(getMockData());
    } finally {
      setLoading(false);
    }
  };

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
    fetchData();
  };

  const renderStatsModalContent = () => {
    if (!selectedStatsDetail || !dashboardData) return null;

    const stats = dashboardData.statistics || {};
    
    switch (selectedStatsDetail) {
      case 'totalPredictions':
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Total Predictions Details</h3>
            <div className="space-y-2">
              <p className="flex justify-between">
                <span>Total predictions made:</span>
                <strong>{stats.total_predictions || 0}</strong>
              </p>
              <p className="text-sm text-gray-500 mt-2">
                This includes all cardiovascular risk assessments performed {user?.role === 'doctor' ? 'for your patients' : 'for your health'}.
              </p>
            </div>
          </>
        );
      case 'recentActivity':
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Recent Activity Details</h3>
            <div className="space-y-2">
              <p className="flex justify-between">
                <span>Recent predictions (7 days):</span>
                <strong>{stats.recent_predictions || 0}</strong>
              </p>
              <p className="flex justify-between">
                <span>Total activity:</span>
                <strong>{stats.recent_activity || 0}</strong>
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Activity includes predictions, profile updates, and system interactions.
              </p>
            </div>
          </>
        );
      case 'totalPatients':
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Total Patients Details</h3>
            <div className="space-y-2">
              <p className="flex justify-between">
                <span>Patients under your care:</span>
                <strong>{stats.total_patients || 0}</strong>
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Active patients who have received cardiovascular risk assessments.
              </p>
            </div>
          </>
        );
      case 'highRisk':
        return (
          <>
            <h3 className="text-lg font-semibold mb-4">Risk Distribution Details</h3>
            <div className="space-y-3">
              <div className="flex justify-between items-center">
                <span className="flex items-center">
                  <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
                  High Risk:
                </span>
                <strong className="text-red-600">{stats.risk_distribution?.High || 0}</strong>
              </div>
              <div className="flex justify-between items-center">
                <span className="flex items-center">
                  <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
                  Moderate Risk:
                </span>
                <strong className="text-yellow-600">{stats.risk_distribution?.Moderate || 0}</strong>
              </div>
              <div className="flex justify-between items-center">
                <span className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
                  Low Risk:
                </span>
                <strong className="text-green-600">{stats.risk_distribution?.Low || 0}</strong>
              </div>
              <p className="text-sm text-gray-500 mt-2">
                Risk categories based on cardiovascular risk assessment scores.
              </p>
            </div>
          </>
        );
      default:
        return null;
    }
  };

  if (loading) {
    return (
      <DashboardLayout>
        <div className="flex flex-col justify-center items-center h-64 space-y-4">
          <LoadingSpinner />
          <p className="text-gray-600">Loading your dashboard...</p>
        </div>
      </DashboardLayout>
    );
  }

  if (!dashboardData) {
    return (
      <DashboardLayout>
        <div className="flex flex-col justify-center items-center h-64 space-y-4">
          <AlertTriangle className="w-12 h-12 text-yellow-500" />
          <p className="text-gray-600">No dashboard data available</p>
          <button
            onClick={handleRetry}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
          >
            <RefreshCw className="w-4 h-4" />
            <span>Retry</span>
          </button>
        </div>
      </DashboardLayout>
    );
  }

  // Calculate risk distribution for patient
  const patientRiskDistribution = dashboardData.statistics?.risk_distribution || {};
  const totalRiskCases = Object.values(patientRiskDistribution).reduce((a, b) => a + b, 0);
  const highRiskPercentage = totalRiskCases > 0 
    ? Math.round((patientRiskDistribution.High || 0) / totalRiskCases * 100) 
    : 0;

  return (
    <DashboardLayout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-8 w-full">
        {/* Error Banner */}
        {error && (
          <div className="mb-6 bg-yellow-50 border-l-4 border-yellow-400 p-4 rounded-r-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <AlertTriangle className="w-5 h-5 text-yellow-400 mr-2" />
                <p className="text-sm text-yellow-800">{error}</p>
              </div>
              <button
                onClick={handleRetry}
                className="text-yellow-600 hover:text-yellow-800 text-sm font-medium flex items-center space-x-1"
              >
                <RefreshCw className="w-4 h-4" />
                <span>Retry</span>
              </button>
            </div>
          </div>
        )}

        <DashboardHeader />

        {/* Stats Cards - Responsive Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 md:gap-6 mb-6 sm:mb-8">
          <StatsCard
            title="Total Predictions"
            value={dashboardData.statistics?.total_predictions || 0}
            trend={12}
            icon="chartBar"
            onViewDetails={() => setSelectedStatsDetail('totalPredictions')}
          />
          
          <StatsCard
            title={user?.role === 'doctor' ? "Recent Activity" : "Recent Predictions"}
            value={
              user?.role === 'doctor' 
                ? dashboardData.statistics?.recent_activity || 0 
                : dashboardData.statistics?.recent_predictions || 0
            }
            trend={5}
            icon="clock"
            onViewDetails={() => setSelectedStatsDetail('recentActivity')}
          />
          
          {user?.role === 'doctor' ? (
            <StatsCard
              title="Total Patients"
              value={dashboardData.statistics?.total_patients || 0}
              trend={3}
              icon="userGroup"
              onViewDetails={() => setSelectedStatsDetail('totalPatients')}
            />
          ) : (
            <StatsCard
              title="High Risk %"
              value={highRiskPercentage}
              trend={-2}
              icon="exclamation"
              isRisk={true}
              valueSuffix="%"
              onViewDetails={() => setSelectedStatsDetail('highRisk')}
            />
          )}
          
          <StatsCard
            title="High Risk Cases"
            value={dashboardData.statistics?.risk_distribution?.High || 0}
            trend={8}
            icon="shieldExclamation"
            isRisk={true}
            onViewDetails={() => setSelectedStatsDetail('highRisk')}
          />
        </div>

        {/* Main Content - Responsive Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 md:gap-6 mb-6 sm:mb-8">
          {/* Risk Gauge */}
          <div className="lg:col-span-1 bg-white rounded-xl shadow-sm border border-gray-200 p-4 md:p-6">
            <h2 className="text-lg md:text-xl font-semibold text-gray-800 mb-4 flex items-center">
              <Heart className="w-5 h-5 mr-2 text-red-500" />
              {user?.role === 'doctor' ? 'Latest Assessment' : 'Your CVD Risk Level'}
            </h2>
            <RiskGauge
              value={dashboardData.latest_prediction?.risk_percentage || 0}
              category={dashboardData.latest_prediction?.risk_category || 'Low'}
            />
            <div className="mt-4 p-3 bg-gray-50 rounded-lg">
              <p className="text-sm text-gray-600">
                {dashboardData.latest_prediction?.risk_category === 'High' ? (
                  <span className="text-red-600 font-medium">
                    ⚠️ High risk detected. Immediate consultation recommended.
                  </span>
                ) : dashboardData.latest_prediction?.risk_category === 'Moderate' ? (
                  <span className="text-yellow-600 font-medium">
                    ⚡ Moderate risk. Consider lifestyle improvements.
                  </span>
                ) : (
                  <span className="text-green-600 font-medium">
                    ✅ Low risk. Maintain healthy habits.
                  </span>
                )}
              </p>
            </div>
          </div>

          {/* Prediction History */}
          <div className="lg:col-span-2 bg-white rounded-xl shadow-sm border border-gray-200 p-4 md:p-6">
            <h2 className="text-lg md:text-xl font-semibold text-gray-800 mb-4 flex items-center">
              <TrendingUp className="w-5 h-5 mr-2 text-blue-500" />
              Prediction History
            </h2>
            <div className="min-h-[200px]">
              <PredictionHistory predictions={dashboardData.recent_predictions || []} />
            </div>
          </div>
        </div>

        {/* Activity Timeline */}
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-4 md:p-6">
          <h2 className="text-lg md:text-xl font-semibold text-gray-800 mb-4 flex items-center">
            <Activity className="w-5 h-5 mr-2 text-green-500" />
            Recent Activity
          </h2>
          <div className="min-h-[150px]">
            <ActivityTimeline activities={dashboardData.recent_activities || []} />
          </div>
        </div>

        {/* Stats Detail Modal */}
        {selectedStatsDetail && (
          <div
            className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4"
            onClick={() => setSelectedStatsDetail(null)}
          >
            <div
              className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4 p-6 max-h-[80vh] overflow-y-auto"
              onClick={(e) => e.stopPropagation()}
            >
              {renderStatsModalContent()}
              <div className="mt-6 flex justify-end space-x-3">
                <button
                  onClick={() => setSelectedStatsDetail(null)}
                  className="px-4 py-2 text-gray-600 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </DashboardLayout>
  );
};

export default Dashboard;
