// src/components/dashboard/RiskGauge.jsx
import React from 'react';
import { getRiskCategoryInfo } from '../../utils/helpers';

const RiskGauge = ({ value, category }) => {
  const categoryInfo = getRiskCategoryInfo(category);
  const gaugeWidth = Math.min(100, Math.max(0, value));

  return (
    <div className="space-y-4">
      <div className="relative pt-1">
        <div className="flex items-center justify-between">
          <div>
            <span className={`inline-block px-2 py-1 text-xs font-semibold ${categoryInfo.color} ${categoryInfo.bgColor} rounded-full`}>
              {categoryInfo.label}
            </span>
          </div>
          <div className="text-right">
            <span className="text-xs font-semibold inline-block text-gray-600">
              {value}%
            </span>
          </div>
        </div>
        <div className="overflow-hidden h-4 mt-2 text-xs flex rounded-full bg-gray-200">
          <div
            style={{ width: `${gaugeWidth}%` }}
            className={`shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center rounded-full ${
              category === 'Low' ? 'risk-low' : 
              category === 'Moderate' ? 'risk-moderate' : 
              'risk-high'
            }`}
          ></div>
        </div>
      </div>
      <div className="flex justify-between text-xs text-gray-500">
        <span>0%</span>
        <span>Low Risk</span>
        <span>Moderate</span>
        <span>High Risk</span>
        <span>100%</span>
      </div>
    </div>
  );
};

export default RiskGauge;
